mapper.insert:

	INSERT INTO
		exerciserecord(erno, edate, eno, erset, ernumber, prno, erdate, ermemo)
	VALUES(
		exerciserecord_seq.NEXTVAL, #{edate}, #{eno}, #{erset}, #{ernumber}, #{prno}, sysdate, #{ermemo}
	)

mapper.delete:

	DELETE
	FROM
		exerciserecord
	WHERE
		prno = #{prno}
		AND edate = #{edate}
